{
  "name": "Test Gmail New Unread Email Trigger",
  "description": "Testing gmail.new_unread_email trigger",
  "triggerServiceType": "GMAIL",
  "triggerType": "gmail.new_unread_email",
  "triggerConfig": {
    "label": "INBOX",
    "subjectContains": "test",
    "fromAddress": ""
  },
  "actions": [
    {
      "serviceType": "DISCORD",
      "reactionType": "discord.send_webhook",
      "reactionConfig": {
        "webhookUrl": "{{discord_webhook_url}}",
        "messageTemplate": "[TEST] Gmail Trigger: New email from {from} - Subject: {subject}"
      },
      "orderIndex": 0
    }
  ]
}

{
