}

{
  "name": "Test GitHub Create Issue Reaction",
  "description": "Testing github.create_issue reaction",
  "triggerServiceType": "GMAIL",
  "triggerType": "gmail.new_unread_email",
  "triggerConfig": {
    "label": "INBOX",
    "subjectContains": "bug"
  },
  "actions": [
    {
      "serviceType": "GITHUB",
      "reactionType": "github.create_issue",
      "reactionConfig": {
        "repository": "{{github_test_repo}}",
        "issueTitle": "[Auto] Bug Report from Email: {subject}",
        "issueBody": "**Automatically created from email**\n\n**From:** {from}\n**Subject:** {subject}\n\n**Preview:**\n{snippet}\n\n---\n*Created by AREA automation*",
        "labels": "bug,auto-generated"
      },
      "orderIndex": 0
    }
  ]
}

{
  "name": "Test GitHub Create PR Reaction",
  "description": "Testing github.create_pr reaction",
  "triggerServiceType": "GITHUB",
  "triggerType": "github.issue_created",
  "triggerConfig": {
    "repository": "{{github_test_repo}}"
  },
  "actions": [
    {
      "serviceType": "GITHUB",
      "reactionType": "github.create_pr",
      "reactionConfig": {
        "repository": "{{github_test_repo}}",
        "prTitle": "[Auto] Fix for issue #{issueNumber}: {issueTitle}",
        "prBody": "**Automatically created PR**\n\nThis PR addresses issue #{issueNumber}\n\n**Issue Title:** {issueTitle}\n**Issue Author:** {issueAuthor}\n\n---\n*Created by AREA automation*",
        "sourceBranch": "auto/issue-{issueNumber}",
        "targetBranch": "main",
        "filePath": "docs/auto-generated/issue-{issueNumber}.md",
        "fileContent": "# Issue #{issueNumber}\n\n**Title:** {issueTitle}\n**Author:** {issueAuthor}\n**URL:** {issueUrl}\n\n## Description\n{issueBody}",
        "commitMessage": "docs: add documentation for issue #{issueNumber}"
      },
      "orderIndex": 0
    }
  ]
}

{
  "name": "Test GitHub Issue to Issue Chain",
  "description": "Testing github.issue_created -> github.create_issue chain",
  "triggerServiceType": "GITHUB",
  "triggerType": "github.issue_created",
  "triggerConfig": {
    "repository": "{{github_source_repo}}"
  },
  "actions": [
    {
      "serviceType": "GITHUB",
      "reactionType": "github.create_issue",
      "reactionConfig": {
        "repository": "{{github_target_repo}}",
        "issueTitle": "[Mirror] {issueTitle}",
        "issueBody": "**Mirrored from:** {{github_source_repo}}#{issueNumber}\n\n**Original Author:** {issueAuthor}\n**Original URL:** {issueUrl}\n\n---\n\n{issueBody}",
        "labels": "mirrored,tracking"
      },
      "orderIndex": 0
    }
  ]
}

