{
  "name": "Gmail to GitHub Issue - Full Test",
  "description": "Complete test: Gmail email triggers GitHub issue creation",
  "triggerServiceType": "GMAIL",
  "triggerType": "gmail.new_unread_email",
  "triggerConfig": {
    "label": "INBOX",
    "subjectContains": "bug"
  },
  "actions": [
    {
      "serviceType": "GITHUB",
      "reactionType": "github.create_issue",
      "reactionConfig": {
        "repository": "{{github_test_repo}}",
